import React, { useState } from 'react';
import "./services.css";
import photo1 from "../images/Apartment.png";
import photo2 from "../images/service-icon-02.png";
import photo3 from "../images/service-icon-03.png";

const Services = () => {
    const[selectedService,setSelectedService]=useState(null);
    const handleClick=(item)=>{
        setSelectedService(item);
    }
    const servicesData = [
        {
            id: 1,
            photo: photo1,
            text: "Apartment",
        },
        {
            id: 2,
            photo: photo2,
            text: "Food & Life",
        },
        {
            id: 3,
            photo: photo3,
            text: "Cars",
        },
        {
            id: 4,
            photo: photo1,
            text: "Shopping",
        },
        {
            id: 5,
            photo: photo2,
            text: "Traveling",
        },
    ];

    return (
        <>
            <div className="containerS container">
                <div className="sub-cont-part1">
                <h3>OUR SERVICES</h3>
                <h1>
                    What Our Agency <span>Provides</span>
                </h1>
                <div className="list-services">
                    {servicesData.map((item) => (
                        <div key={item.id} 
                        className="service-item"
                        onClick={()=>handleClick(item)}
                        >
                            <img src={item.photo} alt={item.text} />
                            <p>{item.text}</p>
                        </div>
                    ))}
                </div>
                 .sub-cont-part1
                </div>
            </div>
        </>
    );
};

export default Services;
